const greetings=["こんにちは","안녕하세요","Hola","Bonjour","Ciao","Привет","你好","नमस्ते","Hallo","Olá","مرحبا","Γεια σας","สวัสดี","שָׁלוֹם","Xin chào"];class FloatingText{constructor(){this.element=document.createElement("div"),this.element.className="floating-text",document.body.appendChild(this.element),this.startAnimation()}startAnimation(){const t=greetings[Math.floor(Math.random()*greetings.length)];this.element.textContent=t;const e=1.2*Math.random()+.8;this.element.style.fontSize=`${e}rem`;const n=90*Math.random()+5,o=20*Math.random()+90;this.element.style.left=`${n}vw`,this.element.style.top=`${o}vh`;const a=4*Math.random()+4;this.element.style.animation="none",this.element.offsetHeight,this.element.style.animation=`float ${a}s linear`}}const createFloatingTexts=()=>{const t=[];for(let e=0;e<15;e++){const n=new FloatingText;t.push(n),n.element.addEventListener("animationend",(()=>{n.startAnimation()})),setTimeout((()=>{n.startAnimation()}),500*e)}};document.addEventListener("DOMContentLoaded",createFloatingTexts);